import React from "react";
import data from '../data.json'

class Products extends React.Component {
    constructor(props) {
        super()
        this.state = ({
            products: data.products,
        })
    }

    handlesize = (event) => {
        let size = event.target.innerText

        let filtersize = data.products.filter((fsize) => fsize.availableSizes.includes(size))


        this.setState({
            products: filtersize,
        })


        console.log(filtersize)
    }





    render() {
        return (

            <>
                {
                    ["S", "XS", "M", "X", "L", "XL", "XXL"].map((size) => {
                        return (
                            <>
                                <button onClick={(event) => { this.handlesize(event) }}>{size}</button>
                            </>
                        )
                    })
                }
                <div>
                <select>

                    <option>Select</option>
                    <option>
                        Highest to Lowest
                    </option>
                    <option>
                        Lowest  to Highest 
                    </option>
                </select>

                </div>


                {
                    this.state.products.map((product) => {

                        return (

                                <div >
                                     <p>{this.state.products.length} Products find</p> 
                                    <figure>
                                        <img src={'/static/products/' + `${product.sku}` + '_1.jpg'}></img>
                                    </figure>

                                    <p>{product.title}</p>
                                    <p>{product.currencyFormat}{product.price}</p>
                                    <button>Add To Cart</button>
                                </div>

                        )
                    })
                }
            </>
        )
    }



}

export default Products
