import React from "react";
import data from '../data.json'

import Cart from "./cart";

class Products extends React.Component {
    constructor(props) {
        super(props)
        this.state = ({
            products: data.products,
            cart:true,
            list:[],
        })
    }

    handlesize = (event) => {
        let size = event.target.innerText

        let filtersize = data.products.filter((fsize) => fsize.availableSizes.includes(size))


        this.setState({
            products: filtersize,
        })


        console.log(filtersize)
    }

    handlecart=(event)=>{
        let id = event.target.id;

        let ab= data.products.filter((a)=>a.id ==  id).map((a)=>a)
        this.setState({
            data:data.products,
            cart:!this.state.cart,
            list:[ab]


        })
        console.log(ab)
    }


handlerange =(event)=>{
    let range = event.target.value
    let incorder = data.products.sort((a,b)=>b.price - a.price)
    let desorder = data.products.sort((a,b)=>a.price - b.price)


    if(range==="High to Low"){
        this.setState(
            {products:data.products.sort((a,b)=>b.price - a.price)}
        )
    }
     if(range==="Low to High"){
        this.setState({
            products:data.products.sort((a,b)=>a.price - b.price)
        })
    }




    console.log(range)
}



    render() {
        return (

            <>
                {
                    ["S", "XS", "M", "X", "L", "XL", "XXL"].map((size) => {
                        return (
                            <>
                                <button onClick={(event) => { this.handlesize(event) }}>{size}</button>
                            </>
                        )
                    })
                }
                <div>
                Order By
                <select onChange={(event)=>{this.handlerange(event)}}  >

                    <option>Select</option>
                    <option value="High to Low">
                        High to Low
                    </option>
                    <option value="Low to High">
                        Low  to High
                    </option>
                </select>

                {
                    this.state.cart?<Cart  info={this.state}/>:""
                }

                </div>


                <p>{this.state.products.length} Products find</p> 

                {   

                    this.state.products.map((product) => {

                        return (

                                <div id={product.id} >

                                    <figure>
                                        <img src={'/static/products/' + `${product.sku}` + '_1.jpg'}></img>
                                    </figure>

                                    <p>{product.title}</p>
                                    <p>{product.currencyFormat}{product.price}</p>
                                    <button id={product.id} onClick={(event)=>{this.handlecart(event)}}>Add To Cart</button>
                                </div>

                        )
                    })
                }
            </>
        )
    }



}

export default Products
